html {
  font-size: 16px;
}

body {
  height: 100vh;
  width: 100vw;
  margin: 0;
  padding: 0;
  background-color: antiquewhite;
}

#app {
  max-width: 70%;
  height: 100%;
  margin: auto;
  background-color: white;
  font-family: "Poppins" !important;
}

#top-menu {
  height: 50px;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  margin: 5px;
}

#menu-items {
  display: flex;
  flex-direction: row;
  justify-content: space-evenly;
  flex: 30%;
}

#menu-items li {
  list-style-type: none;
  margin-left: 10px;
  margin-right: 10px;
  font-size: 1.6rem;
}

#menu-items li a {
  text-decoration: none;
}

#menu-items li a:visited {
  text-decoration: none;
}

#content-div {
  display: flex;
  flex-direction: column;
}

@media only screen and (orientation: portrait) {
  .product-holder {
    display: flex !important;
    flex-direction: column;
    border: 5px solid green;
  }
}
.product-holder {
  order: -1;
  display: grid;
  grid-template-columns: 2fr 1fr;
  grid-template-rows: 2fr 1fr;
}

.product-image-holder-div {
  display: flex;
  justify-content: center; /* Horizontally center */
  align-items: center; /* Vertically center */
}

.detail-description-title-div {
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
}

.carousel-inner {
  display: flex;
}

.carousel-item {
  margin-right: 0 !important;
  flex: 0 0 33.333333%;
  display: block !important;
}

.carousel-inner {
  padding: 1em;
}

.card {
  margin: 0 0.5em;
  border: none;
}

.carousel-control-prev,
.carousel-control-next {
  color: black !important;
  background-color: #e1e1e1;
  width: 6vh;
  height: 6vh;
  border-radius: 50%;
  top: 50%;
  transform: translateY(-50%);
}
